mapper.select:
SELECT CASE WHEN COUNT(1) > 0 THEN TRUE ELSE FALSE END
FROM course_review
WHERE course_id = #{courseId} AND user_id = #{userId}

mapper.update:
UPDATE course_review 
SET rating = #{rating}, 
    content = #{content}, 
    updated_at = #{updatedAt}
WHERE course_id = #{courseId} AND user_id = #{userId}

UPDATE course_review
SET like_count = like_count + 1
WHERE id = #{reviewId}
