mapper.select:
SELECT CASE WHEN COUNT(1) > 0 THEN TRUE ELSE FALSE END
FROM user
WHERE username = #{username}

SELECT id, username, password, email, avatar, role, status, created_at, updated_at
FROM user
WHERE username = #{username}
  AND password = #{password}
  AND role = #{role}
  AND status = 1
LIMIT 1

SELECT id, username, password, email, avatar, role, status, created_at, updated_at
FROM user
WHERE id = #{id}

SELECT id, username, password, email, avatar, role, status, created_at, updated_at
FROM user
WHERE username = #{username}

SELECT id, username, password, email, avatar, role, status, created_at, updated_at
FROM user
WHERE email = #{email}
